package assignment2.com;

public class Coordinates {
	private int x;
	private int y;

	public Coordinates(int a, int b) {
		// TODO Auto-generated constructor stub
		this.x = a;
		this.y = b;
	}
	
	public int getX()
	{
		return this.x;
	}
	
	public int getY()
	{
		return this.y;
	}

}
